import React, { useState } from 'react';
import { Grid, Card, CardActionArea, CardMedia, Collapse, List, ListItem, ListItemText, Typography } from '@mui/material';

// 假设这是你的专辑数据
const albums = [
  { id: 1, title: 'Album 1', cover: 'cover1.jpg', songs: ['Song 1', 'Song 2', 'Song 3'] },
  { id: 2, title: 'Album 2', cover: 'cover2.jpg', songs: ['Song 4', 'Song 5', 'Song 6'] },
  { id: 3, title: 'Album 3', cover: 'cover3.jpg', songs: ['Song 7', 'Song 8', 'Song 9']},
  { id: 4, title: 'Album 4', cover: 'cover4.jpg', songs: ['Song 10', 'Song 11', 'Song 12']},
  { id: 5, title: 'Album 5', cover: 'cover5.jpg', songs: ['Song 13', 'Song 14', 'Song 15']},
  // 其他专辑...
];

const AlbumPage = () => {
  const [selectedAlbum, setSelectedAlbum] = useState(null);

  const handleAlbumClick = (album) => {
    setSelectedAlbum(selectedAlbum === album ? null : album);
  };

  return (
    <Grid container spacing={2}>
      <Grid item xs={12}>
        <Card>
          <Typography variant="h2" style={{ padding: '20px' }}>All Albums</Typography>
          <Grid container spacing={2} style={{ padding: '20px' }}>
            {albums.map((album) => (
              <Grid item xs={12} sm={6} md={4} key={album.id}>
                <Card variant="outlined" sx={{ padding: 2 }} className="Row">
                  <CardActionArea onClick={() => handleAlbumClick(album)}>
                    <CardMedia component="img" height="400" image={album.cover} alt={album.title} />
                  </CardActionArea>
                </Card>
                {selectedAlbum === album && (
                  <Collapse in={selectedAlbum === album}>
                    <List style={{ paddingLeft: 0 }}>
                      <ListItem>
                        <ListItemText primary={album.title} />
                      </ListItem>
                      {album.songs.map((song, index) => (
                        <ListItem key={index}>
                          <ListItemText primary={song} />
                        </ListItem>
                      ))}
                    </List>
                  </Collapse>
                )}
              </Grid>
            ))}
          </Grid>
        </Card>
      </Grid>
    </Grid>
  );
};

export default AlbumPage;
