// assets
import { IconKey, IconPlaylist } from '@tabler/icons-react';
import config from '../config.json';

// constant
const icons = {
  IconKey, IconPlaylist
};

// ==============================|| EXTRA PAGES MENU ITEMS ||============================== //

// 示例的用户播放列表数据
const userPlaylists = [
  { id: 'playlist1', title: 'Playlist 1', url: 'playlists' },
  { id: 'playlist2', title: 'Playlist 2', url: 'playlists2' },
  // 可以根据需要添加更多的播放列表项
];

// // 根据用户播放列表数据生成菜单项
// const generatePlaylistMenuItems = () => {
//   return userPlaylists.map((playlist) => ({
//     id: playlist.id,
//     title: playlist.title,
//     type: 'item',
//     url: playlist.url,
//     target: false
//   }));
// };

// 根据用户播放列表数据生成菜单项
function generatePlaylistMenuItems() {
  try {
    const token = localStorage.getItem('token');
    if (!token) {
      console.error('Token is null');
      return [];
    }

    const requestOptions = {
      method: 'GET',
      headers: new Headers({
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      }),
      // redirect: 'follow' // 如果您需要处理重定向，可以取消注释
    };

    // 确保使用模板字符串中的`${}`来动态插入服务器主机和端口
    const response = fetch(`http://${config.server_host}:${config.server_port}/api/playlists`, requestOptions);
    console.log('response:', response);

    if (!response.ok) {
      console.error('Failed to fetch playlists:', response.statusText);
      return [];
    }

    // 确保在调用json()方法时使用await，因为它返回一个Promise
    const userPlaylists = response.json();
    console.log('userPlaylists:', userPlaylists);
    // 检查userPlaylists是否为期望的数组格式
    if (!Array.isArray(userPlaylists)) {
      console.error('Playlists data is not an array');
      return [];
    }

    userPlaylists.map((playlist) => ({
      id: playlist.playlist_id,
      title: playlist.name,
      type: 'item',
      url: `/playlist/${playlist.playlist_id}`, // 假设这是对应的前端路由
      target: false
    }));

    return [userPlaylists];

  } catch (error) {
    console.error('Error during fetching playlists:', error);
    return [];
  }
}



const pages = {
  id: 'pages',
  // title: 'PlayLists',
  // caption: 'All Musics',
  type: 'group',
  children: [
    {
      id: 'playlists',
      title: 'Playlists',
      type: 'collapse',
      icon: icons.IconPlaylist,
      children:
        generatePlaylistMenuItems()
      // 使用动态生成的播放列表项
    },

    {
      id: 'authentication',
      title: 'Authentication',
      type: 'collapse',
      icon: icons.IconKey,

      children: [
        {
          id: 'login3',
          title: 'Login',
          type: 'item',
          url: '/pages/login/login3',
          target: true
        },
        {
          id: 'register3',
          title: 'Register',
          type: 'item',
          url: '/pages/register/register3',
          target: true
        }
      ]
    },
  ]
};



export default pages;
