// assets
import { IconKey, IconPlaylist } from '@tabler/icons-react';
import config from '../config.json';

// constant
const icons = {
  IconKey, IconPlaylist
};

// ==============================|| EXTRA PAGES MENU ITEMS ||============================== //



// 根据用户播放列表数据生成菜单项
async function generatePlaylistMenuItems() {

  try {
    const token = localStorage.getItem('token');
    if (token === null) {
      console.error('Token is null');
      return [];
    }
    const headers = new Headers();
    headers.append('Authorization', `Bearer ${token}`);
    headers.append('Content-Type', 'application/json');
    const requestOptions = {
      method: 'GET',
      headers: headers,
      // redirect: 'follow'
    };

    const response = await fetch(`http://${config.server_host}:${config.server_port}/api/playlists`, requestOptions);
    console.log('response:', response);

    if (!response.ok) {
      console.error('Failed to fetch playlists:', response.statusText);
      return [];
    }
    const userPlaylists = response.json();

    return userPlaylists.map((playlist) => ({
      id: playlist.id,
      title: playlist.title,
      type: 'item',
      url: playlist.url,
      target: false
    }));

  } catch (error) {
    console.error('Error during fetching playlists:', error.message);
    return [];
  }

}

const pages = {
  id: 'pages',
  // title: 'PlayLists',
  // caption: 'All Musics',
  type: 'group',
  children: [
    {
      id: 'playlists',
      title: 'Playlists',
      type: 'collapse',
      icon: icons.IconPlaylist,
      children:
        generatePlaylistMenuItems()
      // 使用动态生成的播放列表项
    },

    {
      id: 'authentication',
      title: 'Authentication',
      type: 'collapse',
      icon: icons.IconKey,

      children: [
        {
          id: 'login3',
          title: 'Login',
          type: 'item',
          url: '/pages/login/login3',
          target: true
        },
        {
          id: 'register3',
          title: 'Register',
          type: 'item',
          url: '/pages/register/register3',
          target: true
        }
      ]
    },
  ]
};

export default pages;
