// material-ui
import { Typography } from '@mui/material';

// project imports
import NavGroup from './NavGroup';
import menuItem from 'menu-items';

import config from '../../../../config.json';
import React, { useState, useEffect } from 'react';

// ==============================|| SIDEBAR MENU LIST ||============================== //

async function generatePlaylistMenuItems() {
  try {
    const token = localStorage.getItem('token');
    if (!token) {
      console.error('Token is null');
      return [];
    }

    const requestOptions = {
      method: 'GET',
      headers: new Headers({
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      }),
    };

    const response = await fetch(`http://${config.server_host}:${config.server_port}/api/playlists`, requestOptions);

    if (!response.ok) {
      console.error('Failed to fetch playlists:', response.statusText);
      return [];
    }

    const userPlaylists = await response.json();

    if (!Array.isArray(userPlaylists)) {
      console.error('Playlists data is not an array');
      return [];
    }

    // 使用 map 的返回值，并直接返回它
    return userPlaylists.map((playlist) => ({
      id: playlist.playlist_id,
      title: playlist.name,
      type: 'item',
      url: `/playlist/${playlist.playlist_id}`,
      target: false
    }));

  } catch (error) {
    console.error('Error during fetching playlists:', error);
    return [];
  }
}


const MenuList = () => {

  const [menuLists, setMenuLists] = useState([]);

  useEffect(() => {
    generatePlaylistMenuItems().then((items) => {
      setMenuLists(items);
    });
  }, []);

  console.log('menuLists:', menuLists);

  console.log('menuItem:', menuItem);
  console.log(menuItem.items[1]);
  menuItem.items[1].children[0].children = menuLists;
  console.log(menuItem.items[1].children[0].children);
  const navItems = menuItem.items.map((item) => {
    switch (item.type) {
      case 'group':
        return <NavGroup key={item.id} item={item} />;
      default:
        return (
          <Typography key={item.id} variant="h6" color="error" align="center">
            Menu Items Error
          </Typography>
        );
    }
  });

  return <>{navItems}</>;
};

export default MenuList;
