import React, { useState } from 'react';
import { Grid, Card, CardActionArea, CardMedia, Drawer, List, ListItem, ListItemText } from '@mui/material';

// 假设这是你的专辑数据
const albums = [
  { id: 1, title: 'Album 1', cover: 'cover1.jpg', songs: ['Song 1', 'Song 2', 'Song 3'] },
  { id: 2, title: 'Album 2', cover: 'cover2.jpg', songs: ['Song 4', 'Song 5', 'Song 6'] },
  // 其他专辑...
];

const AlbumPage = () => {
  const [selectedAlbum, setSelectedAlbum] = useState(null);

  const handleAlbumClick = (album) => {
    setSelectedAlbum(album);
  };

  const handleClose = () => {
    setSelectedAlbum(null);
  };

  return (
    <Grid container spacing={2}>
      {albums.map((album) => (
        <Grid item xs={6} sm={4} md={3} key={album.id}>
          <Card>
            <CardActionArea onClick={() => handleAlbumClick(album)}>
              <CardMedia component="img" height="200" image={album.cover} alt={album.title} />
            </CardActionArea>
          </Card>
        </Grid>
      ))}

      {/* 侧边栏显示专辑中的歌曲 */}
      <Drawer anchor="down" open={!!selectedAlbum} onClose={handleClose}>
        {selectedAlbum && (
          <List>
            <ListItem>
              <ListItemText primary={selectedAlbum.title} />
            </ListItem>
            {selectedAlbum.songs.map((song, index) => (
              <ListItem key={index}>
                <ListItemText primary={song} />
              </ListItem>
            ))}
          </List>
        )}
      </Drawer>
    </Grid>
  );
};

export default AlbumPage;
