import React, { useState } from 'react';
import { Grid, Card, CardActionArea, CardMedia, Collapse, List, ListItem, ListItemText } from '@mui/material';

// 假设这是你的专辑数据
const albums = [
  { id: 1, title: 'Album 1', cover: 'cover1.jpg', songs: ['Song 1', 'Song 2', 'Song 3'] },
  { id: 2, title: 'Album 2', cover: 'cover2.jpg', songs: ['Song 4', 'Song 5', 'Song 6'] },
  { id: 3, title: 'Album 3', cover: 'cover3.jpg', songs: ['Song 7', 'Song 8', 'Song 9']},
  { id: 4, title: 'Album 4', cover: 'cover4.jpg', songs: ['Song 10', 'Song 11', 'Song 12']},
  { id: 5, title: 'Album 5', cover: 'cover5.jpg', songs: ['Song 13', 'Song 14', 'Song 15']},
  // 其他专辑...
];

const AlbumPage = () => {
  const [selectedAlbum, setSelectedAlbum] = useState(null);

  const handleAlbumClick = (album) => {
    setSelectedAlbum(selectedAlbum === album ? null : album);
  };

  return (
    <Grid container spacing={2}>
      {albums.map((album, index) => (
        <React.Fragment key={album.id}>
          {index % 4 === 0 && (
            <Grid item xs={12}>
              <Grid container spacing={2}>
                {[0, 1, 2, 3].map((i) => {
                  const albumIndex = index + i;
                  const currentAlbum = albums[albumIndex];
                  if (!currentAlbum) return null;
                  return (
                    <Grid item xs={12} sm={6} md={3} key={currentAlbum.id}>
                      <Card>
                        <CardActionArea onClick={() => handleAlbumClick(currentAlbum)}>
                          <CardMedia component="img" height="350" image={currentAlbum.cover} alt={currentAlbum.title} />
                        </CardActionArea>
                      </Card>
                      <Collapse in={selectedAlbum === currentAlbum}>
                        <List>
                          <ListItem>
                            <ListItemText primary={currentAlbum.title} />
                          </ListItem>
                          {currentAlbum.songs.map((song, songIndex) => (
                            <ListItem key={songIndex}>
                              <ListItemText primary={song} />
                            </ListItem>
                          ))}
                        </List>
                      </Collapse>
                    </Grid>
                  );
                })}
              </Grid>
            </Grid>
          )}
        </React.Fragment>
      ))}
    </Grid>
  );
};

export default AlbumPage;
