import React, { useState } from 'react';
import { Grid, Card, CardActionArea, CardMedia, Dialog, DialogTitle, DialogContent, List, ListItem, ListItemText } from '@mui/material';

// 假设这是你的专辑数据
const albums = [
  { id: 1, title: 'Album 1', cover: 'cover1.jpg', songs: ['Song 1', 'Song 2', 'Song 3'] },
  { id: 2, title: 'Album 2', cover: 'cover2.jpg', songs: ['Song 4', 'Song 5', 'Song 6'] },
  { id: 3, title: 'Album 3', cover: 'cover3.jpg', songs: ['Song 7', 'Song 8', 'Song 9'] },
  { id: 4, title: 'Album 4', cover: 'cover4.jpg', songs: ['Song 10', 'Song 11', 'Song 12']},
  { id: 5, title: 'Album 5', cover: 'cover5.jpg', songs: ['Song 13', 'Song 14', 'Song 15']},
  // 其他专辑...
];

const AlbumPage = () => {
  const [selectedAlbum, setSelectedAlbum] = useState(null);

  const handleAlbumClick = (album) => {
    setSelectedAlbum(album);
  };

  const handleClose = () => {
    setSelectedAlbum(null);
  };

  return (
    <Grid container spacing={2}>
      {albums.map((album) => (
        <Grid item xs={6} sm={4} md={3} key={album.id}>
          <Card>
            <CardActionArea onClick={() => handleAlbumClick(album)}>
              <CardMedia component="img" height="200" image={album.cover} alt={album.title} />
            </CardActionArea>
          </Card>
        </Grid>
      ))}

      {/* 弹出的对话框显示专辑中的歌曲 */}
      <Dialog open={!!selectedAlbum} onClose={handleClose}>
        {selectedAlbum && (
          <>
            <DialogTitle>{selectedAlbum.title}</DialogTitle>
            <DialogContent>
              <List>
                {selectedAlbum.songs.map((song, index) => (
                  <ListItem key={index}>
                    <ListItemText primary={song} />
                  </ListItem>
                ))}
              </List>
            </DialogContent>
          </>
        )}
      </Dialog>
    </Grid>
  );
};

export default AlbumPage;
