// material-ui
import { Typography } from '@mui/material';

// project imports
import NavGroup from './NavGroup';
import menuItem from 'menu-items';

import config from '../../../../config.json';
import { useEffect, useState } from 'react';

// ==============================|| SIDEBAR MENU LIST ||============================== //




async function generatePlaylistMenuItems() {
  try {
    const tokenObj = JSON.parse(localStorage.getItem('token'));
    const token = tokenObj?.token;

    if (!token) {
      console.error('Token is null');
      return [];
    }

    const requestOptions = {
      method: 'GET',
      headers: new Headers({
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      }),
    };

    const response = await fetch(`http://${config.server_host}:${config.server_port}/api/playlists`, requestOptions);

    if (!response.ok) {
      console.error('Failed to fetch playlists:', response.statusText);
      return [];
    }

    const userPlaylists = await response.json();

    if (!Array.isArray(userPlaylists)) {
      console.error('Playlists data is not an array');
      return [];
    }

    // 使用 map 的返回值，并直接返回它
    return userPlaylists.map((playlist) => ({
      id: playlist.playlist_id,
      title: playlist.name,
      type: 'item',
      url: `/playlists/${playlist.playlist_id}`,
      target: false
    }));

  } catch (error) {
    console.error('Error during fetching playlists:', error);
    return [];
  }
}



const MenuList = () => {


  const [playlistItems, setPlaylistItems] = useState(menuItem.items[1].children[0].children);
  useEffect(() => {
    // 假设generatePlaylistMenuItems是一个异步函数，用于从后端获取播放列表数据
    generatePlaylistMenuItems().then(items => {
      // 创建一个新的 menuItem 对象，将获取的数据更新到相应位置
      const updatedMenuItem = {
        ...menuItem,
        items: [
          ...menuItem.items.slice(0, 1), // 复制前面的子项
          {
            ...menuItem.items[1], // 复制第二个子项
            children: [
              {
                ...menuItem.items[1].children[0], // 复制第一个子子项
                children: items // 将获取的数据更新到子子项中
              },
              ...menuItem.items[1].children.slice(1) // 复制其他子子项
            ]
          },
          ...menuItem.items.slice(2) // 复制后面的子项
        ]
      };

      console.log(updatedMenuItem.items[1].children[0].children);

      setPlaylistItems(updatedMenuItem); // 使用更新后的 menuItem 更新状态
    });
  }, []);

  console.log(playlistItems);
  // menuItem.items[1].children[0].children[1] = {
  //   id: 'login32',
  //   title: 'Login',
  //   type: 'item',
  //   url: '/pages/login/login3',
  //   target: true
  // }
  console.log(menuItem.items[1].children[0].children);



  const navItems = menuItem.items.map((item) => {
    switch (item.type) {
      case 'group':
        return <NavGroup key={item.id} item={item} />;
      default:
        return (
          <Typography key={item.id} variant="h6" color="error" align="left">
            Menu Items Error
          </Typography>
        );
    }
  });

  return <>{navItems}</>;
};

export default MenuList;
